import React from "react";
import "./App.css";
import { makeDeck, makeShuffledDeck } from "./utils.js";
import p_types from "./images/pokemon_type.png";
import DisplayType from "./displayType";

class App extends React.Component {
  constructor(props) {
    // Always call super with props in constructor to initialise parent class
    super(props);
    this.state = {
      //Start game
      gameStart: false,
      // Set default value of card deck to new shuffled deck
      cardDeck: makeDeck(),
      cardDeck2: makeShuffledDeck(),
      // currCards holds the cards from the current round
      currAtk: [],
      currDef: [],

      // State to track who won from the current round
      currWinner: null,

      // State to track the score of each player
      scoreboard: [0, 0],
      //Switch attack and defend
      p1Atk: true,
      roundNum: 0,
    };
  }
  rN = 0;
  // Code to determine who won, Player 1 or 2
  determineWinner = (atk, def) => {
    let i = atk[0].index;
    let j = def[0].index;
    let res = this.typeMatchUpChart[i][j];

    switch (res) {
      case 4:
        this.updateScore(1);
        break;
      case 2:
        this.updateScore(2);
        break;
      case 1:
        this.updateScore(3);
        break;
      case 0:
        this.updateScore(4);
        break;
      default:
        return;
    }
  };

  updateScore = (n) => {
    const { scoreboard } = this.state;
    //const {currWinner} = this.state;
    const { p1Atk } = this.state;

    if (p1Atk === true) {
      if (n === 1) {
        scoreboard[0] += 2;
        this.setState({ currWinner: "Player1" });
      } else if (n === 2) {
        this.setState({ currWinner: "No player" });
      } else if (n === 3) {
        scoreboard[1] += 1;
        this.setState({ currWinner: "Player2" });
      } else if (n === 4) {
        scoreboard[1] += 2;
        this.setState({ currWinner: "Player2" });
      }
    } else {
      if (n === 1) {
        scoreboard[1] += 2;
        this.setState({ currWinner: "Player2" });
      } else if (n === 2) {
        this.setState({ currWinner: "No player" });
      } else if (n === 3) {
        scoreboard[0] += 1;
        this.setState({ currWinner: "Player1" });
      } else if (n === 4) {
        scoreboard[0] += 2;
        this.setState({ currWinner: "Player1" });
      }
    }
    this.setState({ scoreboard });
    this.changeP1Atk();
  };

  dealFire=()=>{
    this.dealType(18);
  }
  dealElectric=()=>{
    this.dealType(17);
  }

  dealType = (index) => {
    const { p1Atk } = this.state;

    this.rN++;
    //Pops a specific card from deck
    if (this.state.cardDeck !== []) {
      const newP1Card = this.state.cardDeck.splice(index, 1);
      const newP2Card = [this.state.cardDeck2.pop()];

      p1Atk
        ? this.setState(
            {
              currAtk: newP1Card,
              currDef: newP2Card,
              roundNum: this.rN,
            },
            () => {
              // Call determineWinner after the state has been updated

              this.determineWinner(newP1Card, newP2Card);
            }
          )
        : this.setState(
            {
              currAtk: newP2Card,
              currDef: newP1Card,
              roundNum: this.rN,
            },
            () => {
              // Call determineWinner after the state has been updated

              this.determineWinner(newP2Card, newP1Card);
            }
          );
    } else {
      return (
        <p>
          The deck is out of cards. Please click 'restart to restart the game.
        </p>
      );
    }
  };

  dealCards = () => {
    const { p1Atk } = this.state;
    this.rN++;
    // this.state.cardDeck.pop() modifies this.state.cardDeck array
    if (this.state.cardDeck !== []) {
      const newP1Card = [this.state.cardDeck.pop()];
      const newP2Card = [this.state.cardDeck2.pop()];

      p1Atk
        ? this.setState(
            {
              currAtk: newP1Card,
              currDef: newP2Card,
              roundNum: this.rN,
            },
            () => {
              // Call determineWinner after the state has been updated

              this.determineWinner(newP1Card, newP2Card);
            }
          )
        : this.setState(
            {
              currAtk: newP2Card,
              currDef: newP1Card,
              roundNum: this.rN,
            },
            () => {
              // Call determineWinner after the state has been updated

              this.determineWinner(newP2Card, newP1Card);
            }
          );
    } else {
      return (
        <p>
          The deck is out of cards. Please click 'restart to restart the game.
        </p>
      );
    }
  };
  startGame = () => {
    this.setState({ gameStart: true });
  };
  restartGame = () => {
    this.rN = 0;
    this.setState({
      cardDeck: makeDeck(),
      cardDeck2: makeShuffledDeck(),
      currAtk: [],
      currDef: [],
      currWinner: null,
      scoreboard: [0, 0],
      p1Atk: true,
      roundNum: 0,
      gameStart: false,
    });
  };
  changeP1Atk = () => {
    const { p1Atk } = this.state;
    p1Atk ? this.setState({ p1Atk: false }) : this.setState({ p1Atk: true });
  };

  typeMatchUpChart = [
    /*typeless, Normal","Fighting","Psychic","Dark","Ghost","Bug","Dragon",
     "Flying","Fairy","Rock","Ground","Steel","Poison","Grass","Water","Ice",
     "Electric","Fire"*/
    [2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2],
    [2, 2, 2, 2, 2, 0, 2, 2, 2, 2, 1, 2, 1, 2, 2, 2, 2, 2, 2],
    [2, 4, 2, 1, 4, 0, 1, 2, 1, 1, 4, 2, 4, 1, 2, 2, 4, 2, 2],
    [2, 2, 4, 1, 0, 2, 2, 2, 2, 2, 2, 2, 1, 4, 2, 2, 2, 2, 2],
    [2, 2, 1, 4, 1, 4, 2, 2, 2, 1, 2, 2, 2, 2, 2, 2, 2, 2, 2],
    [2, 0, 2, 4, 1, 4, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2, 2],
    [2, 2, 1, 4, 4, 1, 2, 2, 1, 1, 2, 2, 1, 1, 4, 2, 2, 2, 1],
