import React from "react";

class DisplayType extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render(){
    const {cardDeck} = this.props;
    const picArray = cardDeck.map(({type})=> require(`./images/${type}.png`))
    return(picArray)
  }


}

export default DisplayType;
