import React from "react";

class DisplayType extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  render(){
    const {cardDeck} = this.props;
    console.log(cardDeck.length);
    let picArray=[];
    //let typeLess = require(`./images/Typeless.png`);
    //picArray.push(typeLess);
    for (let i=1;i<cardDeck.length;i++){
      let cType = cardDeck[i].type;
      let myImage = require(`./images/${cType}.png`);
      picArray.push(myImage);
    }

    return(<div>{cardDeck.map(({index, type})=><img src={picArray[index]} width="50" alt={type} />)}</div>)
  }


}

export default DisplayType;
